mapper.update:
update user


        nickname = #{nickname},


        avatar_url = #{avatarUrl},


        phone = #{phone}


where id = #{id}

update user
set balance = balance + #{amount}
where id = #{id}

update user
set balance = balance - #{actualDeduction}
where id = #{userId}

mapper.select:
select id
from user
where openid = #{openid}

select balance
from user
where id = #{id}
